import React, { useState, useEffect, useRef } from 'react';
import './Gallery.css';

const images = [
  {
    id: 1,
    src: 'https://picsum.photos/id/237/200/300',
    alt: 'Image 1'
  },
  {
    id: 2,
    src: 'https://picsum.photos/id/238/200/300',
    alt: 'Image 2'
  },
  {
    id: 3,
    src: 'https://picsum.photos/id/239/200/300',
    alt: 'Image 3'
  },
  {
    id: 4,
    src: 'https://picsum.photos/id/240/200/300',
    alt: 'Image 4'
  },
  {
    id: 5,
    src: 'https://picsum.photos/id/241/200/300',
    alt: 'Image 5'
  },
  {
    id: 6,
    src: 'https://picsum.photos/id/242/200/300',
    alt: 'Image 6'
  }
];

const Gallery = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const timeoutRef = useRef(null);

  useEffect(() => {
    timeoutRef.current = setTimeout(
      () =>
        setCurrentIndex((prevIndex) =>
          prevIndex === images.length - 1 ? 0 : prevIndex + 1
        ),
      5000
    );

    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, [currentIndex]);

  return (
    <div className="gallery-container">
      <h2 className="gallery-heading">Photo Gallery</h2>
      <div className="gallery-slider">
        {images.map((image, index) => (
          <div
            key={image.id}
            className={`gallery-slide ${
              index === currentIndex ? 'active' : ''
            }`}
          >
            <img src={image.src} alt={image.alt} />
          </div>
        ))}
      </div>
      <div className="gallery-thumbnails">
        {images.map((image, index) => (
          <div
            key={image.id}
            className={`gallery-thumbnail ${
              index === currentIndex ? 'active' : ''
            }`}
            onClick={() => setCurrentIndex(index)}
          >
            <img src={image.src} alt={image.alt} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default Gallery;
