import React, { useState } from 'react';
import './Gallery.css';


const photos = [
  { url: 'https://via.placeholder.com/300x200', caption: 'Photo 1' },
  { url: 'https://via.placeholder.com/300x200', caption: 'Photo 2' },
  { url: 'https://via.placeholder.com/300x200', caption: 'Photo 3' },
  { url: 'https://via.placeholder.com/300x200', caption: 'Photo 4' },
  { url: 'https://via.placeholder.com/300x200', caption: 'Photo 5' },
];

const Gallery = () => {
  const [index, setIndex] = useState(0); // Index of current photo
  
  const handlePrev = () => {
    setIndex((index - 1 + photos.length) % photos.length); // Wrap around if index is less than 0
  };
  
  const handleNext = () => {
    setIndex((index + 1) % photos.length); // Wrap around if index is greater than or equal to photo length
  };
  
  return (
    <div className="gallery-container">
      <img className="gallery-image" src={photos[index].url} alt={photos[index].caption} />
      <div className="gallery-buttons">
        <button className="gallery-button" onClick={handlePrev}>Prev</button>
        <button className="gallery-button" onClick={handleNext}>Next</button>
      </div>
    </div>
  );
};

export default Gallery;
