import React, { useState } from 'react';
import './Gallery.css';

const images = [
  {
    id: 1,
    src: 'https://picsum.photos/id/237/600/400',
    alt: 'Image 1'
  },
  {
    id: 2,
    src: 'https://picsum.photos/id/238/600/400',
    alt: 'Image 2'
  },
  {
    id: 3,
    src: 'https://picsum.photos/id/239/600/400',
    alt: 'Image 3'
  },
  {
    id: 4,
    src: 'https://picsum.photos/id/240/600/400',
    alt: 'Image 4'
  },
  {
    id: 5,
    src: 'https://picsum.photos/id/241/600/400',
    alt: 'Image 5'
  },
  {
    id: 6,
    src: 'https://picsum.photos/id/242/600/400',
    alt: 'Image 6'
  }
];

const Gallery = () => {
  const [activeIndex, setActiveIndex] = useState(0);

  const handleNext = () => {
    setActiveIndex((activeIndex + 1) % images.length);
  };

  const handlePrev = () => {
    setActiveIndex((activeIndex + images.length - 1) % images.length);
  };

  return (
    <div className="gallery-container">
      <h2 className="gallery-heading">Photo Gallery</h2>
      <div className="gallery-slideshow">
        <div className="gallery-slideshow-inner" style={{ transform: `translateX(-${activeIndex * 100}%)` }}>
          {images.map((image) => (
            <div key={image.id} className="gallery-slideshow-item">
              <img src={image.src} alt={image.alt} />
            </div>
          ))}
        </div>
        <button className="gallery-slideshow-prev" onClick={handlePrev}>
          &lt;
        </button>
        <button className="gallery-slideshow-next" onClick={handleNext}>
          &gt;
        </button>
      </div>
    </div>
  );
};

export default Gallery;
