import React, { useState, useEffect, useRef } from 'react';
import './Gallery.css';

const images = [
  {
    id: 1,
    src: 'https://picsum.photos/id/237/600/400',
    alt: 'Image 1',
    caption: 'A beautiful landscape'
  },
  {
    id: 2,
    src: 'https://picsum.photos/id/238/600/400',
    alt: 'Image 2',
    caption: 'A stunning sunset'
  },
  {
    id: 3,
    src: 'https://picsum.photos/id/239/600/400',
    alt: 'Image 3',
    caption: 'A cute puppy'
  },
  {
    id: 4,
    src: 'https://picsum.photos/id/240/600/400',
    alt: 'Image 4',
    caption: 'A delicious meal'
  },
  {
    id: 5,
    src: 'https://picsum.photos/id/241/600/400',
    alt: 'Image 5',
    caption: 'A majestic bird'
  },
  {
    id: 6,
    src: 'https://picsum.photos/id/242/600/400',
    alt: 'Image 6',
    caption: 'A breathtaking view'
  }
];

const Gallery = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isZoomed, setIsZoomed] = useState(false);
  const timeoutRef = useRef(null);

  useEffect(() => {
    timeoutRef.current = setTimeout(
      () =>
        setCurrentIndex((prevIndex) =>
          prevIndex === images.length - 1 ? 0 : prevIndex + 1
        ),
      5000
    );

    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, [currentIndex]);

  const handleThumbnailClick = (index) => {
    setCurrentIndex(index);
  };

  const handleZoomToggle = () => {
    setIsZoomed(!isZoomed);
  };

  return (
    <article>
    </article>
    
  );
};

export default Gallery;
